mapper.select:
select group_name group_name , group_code group_code , to_char(group_codedate, 'yyyy-mm-dd') group_codedate , to_char(group_codedate+7, 'yyyy-mm-dd') group_codedate7 from bnd_group where
group_no = #{group_no}
group_code = #{group_code}
and group_codedate between sysdate-7 and sysdate

mapper.insert:

	select bnd_group_no_seq.nextval from dual

insert into bnd_group(
	group_no
	, group_name
	, group_category
	, group_subcategory	
) values(
	#{group_no}
	, #{group_name}
	, #{group_category}
	, #{group_subcategory}		
)

insert into bnd_groupjoin(
	group_no
	, member_id
	, member_level
) values (
	#{group_no}
	, #{member_id}
	, 3
)
