mapper.insert:
insert into
	board(
	 	board_no
	 	, member_id
	 	, board_title
	 	, board_content

		 	, board_savedfile
		 	, board_originfile

	 )values(
	 	board_seq.nextval
	 	, #{member_id}
	 	, #{board_title}
	 	, #{board_content}

		 	, #{board_savedfile}
		 	, #{board_originfile}

	 )

mapper.delete:
delete from
	board
where
	board_no = #{board_no}
and
	member_id = #{member_id}

mapper.update:
update
	board
set
	board_title = #{board_title}
	, board_content = #{board_content}

	, board_savedfile = #{board_savedfile}
	, board_originfile = #{board_originfile}

where
	board_no = #{board_no}
and
	member_id = #{member_id}

mapper.insert:
insert into
	reply(
		reply_no
		, board_no
		, reply_content
		, member_id
	)values(
		reply_seq.nextval
		, #{board_no}
		, #{reply_content}
		, #{member_id}		
	)

mapper.delete:
delete from
	reply
where
	member_id = #{member_id}
and
	reply_no = #{reply_no}
